// template block should return single DOM node
<template>
  <div>
    <div> value: {{ counter }} </div>
    <button v-on:click="incrementCounter">Add</button>
    <button
     v-on:click="decrementCounter"
     v-bind:disabled="counter === 0"
     >Subtract</button>
  </div>
</template>

<script>
export default  {
  data() {
    return {
      counter: 0,
    };
  },
  methods: {
    incrementCounter() {
       this.counter += 1;
    },
    decrementCounter() {
      this.counter -= 1;
    }
  }
}
</script>

<style>
body {
  background: #2e2e2e;
  color: #f9f9f9;
}
</style>
